mapper.insert:
INSERT INTO
    customer (
          id
        , firstname
        , lastname
        , age
    )
VALUES (
      null
    , #{firstname}
    , #{lastname}
    , #{age}
)

mapper.update:
UPDATE
    customer
SET
      firstname = #{firstname}
    , lastname = #{lastname}
    , age = #{age}
WHERE 
    id = #{id}
